import React from "react";
import logo from "./res/logo.png";

import "./components/App.css";
import "./bootstrap.min.css";
import "./styles/animate.min.css";

import styled from "styled-components";
import Fade from "react-reveal/Fade";

import Wow from "react-wow";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faQuoteRight } from "@fortawesome/free-solid-svg-icons";

const Logo = styled.img`
  width: 70%;
  margin: 15px;
  margin-top: -10%;
`;

const Authors = styled.h3`
  font-size: 2em;
`;

const Title = styled.h1`
  margin: 20px;
  font-size: 4.5em;
`;

const Icone = styled(Title)``;

const Content = styled.p`
  font-size: 1.5em;
  padding: 0px 50px 0px 50px;
  margin-bottom: 50px;
`;

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Fade top>
          <Logo src={logo} alt="Logo de Bonap" />
        </Fade>
        <Fade right>
          <Authors>LE FEYER Aymeric</Authors>
        </Fade>
        <Fade left>
          <Authors>CARRY Quentin</Authors>
        </Fade>
      </header>

      <Wow animation="fadeInUp">
        <Title>
          <Icone>
            <FontAwesomeIcon icon={faQuoteRight} />
          </Icone>
          Introduction
        </Title>
      </Wow>

      <Wow animation="lightSpeedIn">
        <Content>
          Bonap est un gestionnaire de repas intelligent, c'est avant tout le
          projet de fin d'etudes de deux etudiants de licence informatique en
          2020.
        </Content>
      </Wow>
    </div>
  );
}

export default App;
